import { ChangeEvent, useState } from 'react';

interface PaymentMethodProps {

}

const PaymentMethod: React.FC<PaymentMethodProps> = () => {
    const [paymentMethod, setPaymentMethod] = useState('');

    const handleChangePayment = (e: ChangeEvent<HTMLElement>) => {
        setPaymentMethod(prev => ({
            ...prev,
            [e.target.id]: e.target.check
        }))
    }

    return (  
        <div
                area-label="payment_method"
                className="px-3 py-2 border rounded-lg border-slate-400"
            >
                <p>Please choose your payment method</p>
                <form>
                    <div>
                        <input 
                            type="radio" 
                            name="payment_method" 
                            id="creditCard"
                            className="mt-4 mr-2"
                            checked={paymentMethod.creditCard}
                            onChange={handleChangePayment}
                        />
                        <label htmlFor="Creditcard">Credit Card</label>
                    </div>
                    <div>
                        <input 
                            type="radio" 
                            name="payment_method" 
                            id="paypal"
                            className="mt-4 mr-2"
                            checked={paymentMethod.paypal}
                            onChange={handleChangePayment}
                        />
                        <label htmlFor="Paypal">Paypal</label>
                    </div>
                    <div>
                        <input 
                            type="radio" 
                            name="payment_method" 
                            id="klarna"
                            className="mt-4 mr-2" 
                            checked={paymentMethod.klarna}
                            onChange={handleChangePayment}
                        />
                        <label htmlFor="Klarna">Klarna</label>
                    </div>
                </form>
        </div>
    );
}

export default PaymentMethod;
